pragma solidity ^0.5.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Capped.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Detailed.sol";

contract CommonFanPiao is ERC20, ERC20Detailed, ERC20Burnable, ERC20Capped {
    string public builtOn = "https://www.matataki.io/token";

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 cap,
        uint256 initialSupply
    ) ERC20Detailed(name, symbol, decimals) ERC20Capped(cap) public {
        _mint(msg.sender, initialSupply); // Initial token offer
    }
}
